mapper.select:
SELECT *
FROM db_tars.t_adapter_conf
WHERE
    application = #{application}
    AND server_name = #{serverName}
    AND node_name = #{nodeName}

SELECT *
FROM db_tars.t_adapter_conf
WHERE
application = #{application}
AND server_name = #{serverName}
AND node_name = #{nodeName}
AND adapter_name = concat(application, '.', server_name, '.', #{objName}, 'Adapter')
